CREATE DATABASE IF NOT EXISTS ejercicioDev_db CHARACTER SET utf8 COLLATE utf8_bin;
use ejercicioDev_db;
DROP TABLE IF EXISTS Provincia;
DROP TABLE IF EXISTS Empleado;
CREATE TABLE IF NOT EXISTS Provincia(
	id int NOT NULL AUTO_INCREMENT,
	nombre_provincia varchar(255),
  capital_provincia varchar(255),
  descripcion_provincia text,
  poblacion_provincia varchar(255),
  superficie_provincia varchar(255),
  latitud_provincia varchar(255),
  longitud_provincia varchar(255),
  id_region varchar(255),
	PRIMARY KEY(id)
	);
  CREATE TABLE IF NOT EXISTS Empleado(
  	id int NOT NULL AUTO_INCREMENT,
  	nombre_empleado varchar(255),
    apellido_empleado varchar(255),
    cedula_empleado varchar(255),
    provincia_empleado varchar(255),
    fecha_nacimiento varchar(255),
    email_empleado varchar(255),
    observaciones_personales varchar(255),
    fecha_ingreso varchar(255),
    cargo_empleado varchar(255),
    departamento_empleado varchar(255),
    provincia_labora varchar(255),
    sueldo_empleado varchar(255),
    jornada_parcial varchar(255),
    observaciones_laborales varchar(255),
  	PRIMARY KEY(id)
  	);

INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Azuay','Cuenca','Es llamada la Atenas del Ecuador por su arquitectura, su diversidad cultural, su aporte a las artes, ciencias y letras ecuatorianas y por ser el lugar de nacimiento de muchos personajes ilustres de la sociedad ecuatoriana','569.42','122.00','-2.902222','-79.005261','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Bolivar','Guaranda','Bolívar es una provincia del centro de Ecuador, en la cordillera occidental de los Andes. Su capital es la ciudad de Guaranda. La Provincia de Bolívar se llama así en honor al Libertador Simón Bolívar.','183 641','3254.00','-1.6','-79','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Cañar','Azoguez','La provincia destaca como uno de los sitios turísticos más importantes del país, destacándose entre otros la Fortaleza de Ingapirca, la Laguna de Culebrillas y la ciudad de Azogues.','33848.00','3908.00','-2.733333','-78.833333','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Carchi','Tulcán','Carchi es una provincia ecuatoriana situada al norte del Ecuador en la frontera con Colombia. Su capital es la ciudad de Tulcán. Forma parte de la región 1','82734.00','3783.00','0.811667',' -77.718611','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Chimborazo','Riobamba','En esta provincia se encuentran varias de las cumbres más elevadas del país, como el Carihuairazo, el Altar, Igualata, Sangay, entre otros, que en algunos casos comparte con otras provincias.','223586.00','6487.00','-1.666667','-78.65','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Cotopaxi','Latacunga','La provincia toma el nombre del volcán más grande e importante de su territorio, el volcán Cotopaxi. Cotopaxi se encuentra dividida políticamente en 7 cantones. Según el último ordenamiento territorial, la provincia de Cotopaxi pertenece a la región centro 3','409 205','6569.00','-0.933333',' -78.616667','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('El Oro','Machala','La capital de la provincia de El Oro es la ciudad de Machala, quinta ciudad del país, considerada como la  capital bananera del mundo .','260528.00','6188.00','-3.266669',' -79.9667','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Esmeraldas','Esmeraldas','Provincia del Ecuador situada en su costa noroccidental, conocida popularmente como la provincia verde. Sucapital homónima es uno de los puertos principales del Ecuador y terminal del oleoducto transandino. Posee un aeropuerto para vuelos nacionales e internacionales','189504.00','15 954','-0.966667',' -79.65','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Galápagos','P. Baquerizo Moreno','Es el mayor centro turístico del Ecuador, así como también una de las reservas ecológicas más grandes e importantes del planeta. Con sus 26.640 habitantes, Galápagos es la provincia menos poblada del país, debido principalmente al afán de conservar al máximo la flora y fauna de la región.','5600.00','8010.00','-0.666667',' -90.55','3');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Guayas','Guayaquil','Es el mayor centro comercial e industrial del Ecuador. Con sus 3,8 millones de habitantes, Guayas es la provincia más poblada del país, constituyéndose con el 24,5% de la población de la República.','2526927.00','17139.00','-2.2',' -79.9667','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Imbabura','Ibarra','La provincia también es conocida por sus contrastes poblacionales es así que la población está marcada por diferentes factores demográficos, además desde siempre ha sido núcleo de artesanías y cultura.','181722.00','4599.00','0.35','-78.133333','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Loja','Loja','Loja es una ciudad del Ecuador, capital de la provincia y cantón Loja, tiene una rica tradición en las artes, y por esta razón es conocida como la capital musical y cultural del Ecuador.','206.83','57.00','-3.833','-80.067','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Los Rios','Babahoyo','Los Ríos, oficialmente Provincia de Los Ríos, es una de las 24 provincias de la República del Ecuador, localizada en la Región Costa del país. Su capital es la ciudad de Babahoyo y su localidad más poblada es la ciudad de Quevedo. Es uno de los más importantes centros agrícolas del Ecuador. Su territorio está ubicado en la parte central del litoral del país y limita con las provincias de Guayas, Santo Domingo de los Tsáchilas, Manabí, Cotopaxi y Bolívar. Según el último ordenamiento territorial, la provincia de Los Ríos pertenece a la región comprendida también por las provincias de Bolívar, Guayas y Santa Elena.','778115.00','6254.00','-1.766669','-79.45','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Manabí','Portoviejo','Manabí es una provincia ecuatoriana localizada en el emplazamiento centro-noroeste del Ecuador continental, cuya unidad jurídica se ubica en la región geográfica del litoral, que a su vez se encuentra dividida por el cruce de la línea equinoccial. Su capital es Portoviejo. Limita al oeste con el Océano Pacífico, al norte con la provincia de Esmeraldas, al este con la provincia deSanto Domingo de los Tsáchilas y Los Ríos, al sur con la provincia de Santa Elena y al sur y este con la provincia de Guayas. Con una población de 1,395.249 habitantes, es la tercera provincia más poblada de Ecuador.','1369780.00','18400.00','-1.052219','-80.4506','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Morona Santiago','Macas','Morona Santiago (nombre oficial Provincia de Morona Santiago) es una de las 24 provincias que conforman la República del Ecuador. Es una provincia de la Amazonía ecuatoriana. Su capital es la ciudad de Macas, la cual además, es su urbe más poblada. Su territorio limita al norte con Pastaza, al sur con Zamora Chinchipe, al este con Perú, y al oeste con Tungurahua, Azuay, Chimborazo y Cañar. Ocupa una superficie de 25.690 km², un área similar a la de Cerdeña.','147940.00','25690.00','-2.366667',' -78.133333','4');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Napo','Tena','La provincia de Napo es una de las provincias de la Región Centro Norte (Ecuador), de la República del Ecuador, situada en la región amazónica ecuatoriana e incluyendo parte de las laderas de los Andes, hasta las llanuras amazónicas. Toma su nombre del río Napo. Su capital es la ciudad de Tena. Limita al norte con Sucumbíos, al sur con Pastaza, al oeste con Pichincha,Cotopaxi y Tungurahua y al este con la Provincia de Orellana.','103697.00','13271.00','0.989','-77.8159','4');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Orellana','Francisco de Orellana','Orellana, provincia de la Región Centro Norte (Ecuador), Ecuador, La capital de la provincia es El Coca más conocida como «Coca». Al norte limita con Sucumbíos, al sur con la provincia de Pastaza, al este con Perú y al oeste con Napo. Tiene una superficie de 20.733 km², que en términos de extensión es similar a la de El Salvador. Es una de las provincias más nuevas del país, provincia 22, pues fue creada en 1998 al ser separada de Napo. Inicialmente su nombre iba a ser provincia de Amazonaspero ante un potencial reclamo de Perú se decidió denominarla Orellana. La población es de 136.396 habitantes; sus habitantes nativos pertenecen principalmente a las nacionalidades waorani, schuar y kichwa.','136396.00','20773.00','-0.933333','-75.666667','4');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Pastaza','Puyo','Pastaza, oficialmente Provincia de Pastaza, es una de las 24 provincias que conforman la República del Ecuador, situada en laRegión Amazónica del Ecuador. Recibe su nombre del río Pastaza, que la separa al sur de la provincia de Morona Santiago. Su capital es la ciudad de Puyo.','83933.00','29520.00','-1.066667','-78.001111','4');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Pichincha','Quito','La Provincia de Pichincha es una de las 24 provincias que conforman la República del Ecuador. Se encuentra ubicada al norte del país, en la zona geográfica conocida como sierra. Su capital administrativa es la ciudad de Quito, la cual además es su urbe más poblada y la capital del país. Es también el principal centro comercial del país.','2576287.00','9612.00','-0.25','-78.583333','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Santa Elena','Santa Elena','Santa Elena es una provincia de la costa de Ecuador creada el 7 de noviembre de 2007, la más reciente de las 24 actuales, con territorios que anterior a esa fecha formaban parte de la provincia del Guayas, al oeste de ésta.','308693.00','3763.00','-2.2267',' -80.8583','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Santo Domingo de los Tsáchilas','Santo Domingo','La Provincia de Santo Domingo de los Tsáchilas es una de las provincias de la República del Ecuador y forma parte de laRegión Costa, históricamente conocida como Provincia de Yumbos. Su territorio está en zona de trópico húmedo. La provincia toma su nombre de los Tsáchilas, etnia ancestral de la zona.','410937.00','4180.00','-0.333333','-79.25','2');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Sucumbios','Nueva Loja','Sucumbíos es una provincia del nor-oriente del Ecuador. Su capital es Nueva Loja. Es una de las principales provincias que proveen al Estado ecuatoriano del petróleo que necesita para las exportaciones. Se caracteriza por sus bellos paisajes amazónicos, entre ellos el río Aguarico.','176472.00','18612.00','-0.083333','-76.883333','4');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Tungurahua','Ambato','Tungurahua, oficialmente Provincia de Tungurahua, es una de las 24 provincias que conforman la República del Ecuador. Se encuentra al centro del país, en la región geográfica conocida como sierra. La ciudad de Ambato es su capital administrativa; se divide en 9 cantones. Forma parte de la región comprendida también por las provincias de Cotopaxi, Chimborazo y Pastaza.','504583.00','3334.00','-1.233333','-78.616667','1');INSERT INTO Provincia (nombre_provincia,capital_provincia,descripcion_provincia,poblacion_provincia,superficie_provincia,latitud_provincia,longitud_provincia,id_region) VALUES ('Zamora Chimchipe','Zamora','Zamora Chinchipe es una provincia de Ecuador ubicada en el sur-oriente de la Amazonía ecuatoriana, que limita con la provincia de Morona Santiago al norte; con la provincia de Loja al oeste; y con Perú al sur y este. Según el último ordenamiento territorial del Ecuador, Zamora Chinchipe pertenece a la Región Sur comprendida también por las provincias de Loja y El Oro. Tiene una superficie de 10.556 km², la cual comprende una orografía montañosa única que la distingue del resto de provincias amazónicas.','91376.00','10556.00','-2.883333','-79','4');
